import  { useRef, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import logo from "../../assets/img/logo.png";
import background from "../../assets/img/background.png";
import menu from "../../assets/img/menu.png";
import { useUnityContext} from "react-unity-webgl";
import React, { useEffect } from "react";
import  {Unity } from "react-unity-webgl";

export default function Home(props) {
	const { unityProvider ,isLoaded, loadingProgression } = useUnityContext({
		loaderUrl: "Build/BUILD.loader.js",
		dataUrl: "Build/BUILD.data",
		frameworkUrl: "Build/BUILD.framework.js",
		codeUrl: "Build/BUILD.wasm",
	});



	// const unityContext = new UnityContext({
	// 	loaderUrl: "Build/BUILD.loader.js",
	// 		dataUrl: "Build/BUILD.data",
	// 		frameworkUrl: "Build/BUILD.framework.js",
	// 		codeUrl: "Build/BUILD.wasm",	  });
	const loadingPercentage = Math.round(loadingProgression * 100);
	const [isActive, setIsActive] = useState(false);
	const navigate = useNavigate();
	const handleActive = () => {
		setIsActive(true);
		setTimeout(() => {
			navigate('/menu');
		}, 600);
	};














	return (
		<div className="home">
			<Unity unityProvider={unityProvider}  style={{width:"100%",height:"100vh"}} />


			{console.log(isLoaded)}



		{isLoaded ? <div></div> :<div id="unity-loading-bar">
   <div id="unity-progress-bar-empty">
<div style={{width:`${loadingPercentage}%`}} id="unity-progress-bar-full"></div></div></div> 
	}

















			<div className="header-home">
				<div className="header-menu">
					<div
						rel="noopener noreferrer"
						to="/"
						className="flex items-center px-4 -mb-1 border-b-2 dark:border-transparent dark:text-violet-400 dark:border-violet-400"
					>
						<div className={`detail ${isActive ? 'active' : ''}`} onClick={handleActive} >
							<p>Lĩnh Vực </p>
							<div className="img"  >
								<img src={menu} alt="menu" />
							</div>
						</div>
					</div>
				</div>
			</div>

		</div>
	);
}
