import "./App.scss";
import React, { useLayoutEffect } from "react";
import { Routes, Route, BrowserRouter, useLocation } from "react-router-dom";
import Home from "./pages/Home/Home";
import HomeMenu from "./pages/HomeMenu/HomeMenu";
import { DetailTemplate } from "./templates/DetailTemplate/DetailTemplate";
import About from "./pages/About/About";
import DetailPage from "./pages/DetailPage/DetailPage";

function App() {
  return (
    <BrowserRouter>
      <ScrollToTop>
        <Routes>


        <Route path="/" element={<DetailTemplate />}>
            <Route path=":slug" element={<DetailPage />} />
          </Route>



          <Route path="/menu" element={<HomeMenu />} />
          <Route path="/detail" element={<DetailTemplate />}>
            <Route path=":slug" element={<DetailPage />} />
          </Route>
          <Route path="/about" element={<About />}></Route>

          <Route path="/" element={<Home />} />
        </Routes>
      </ScrollToTop>
    </BrowserRouter>
  );
}

const ScrollToTop = ({ children }) => {
  const location = useLocation();
  useLayoutEffect(() => {
    document.documentElement.scrollTo(0, 0);
  }, [location.pathname]);
  return children;
};

export default App;
