import { useState } from "react";
import { useNavigate } from "react-router-dom";
import menu from "../../assets/img/menu.png";
import React from "react";
import { useUnityContext } from "react-unity-webgl";
import { Unity } from "react-unity-webgl";
export default function Home(props) {
  const navigate = useNavigate();
  window.HelloPage = (name) => {
    navigate(`/${name}`);
  };

  const { unityProvider, isLoaded, loadingProgression } = useUnityContext({
    loaderUrl: "Lotus/Build/Lotus.loader.js",
    dataUrl: "Lotus/Build/Lotus.data",
    frameworkUrl: "Lotus/Build/Lotus.framework.js",
    codeUrl: "Lotus/Build/Lotus.wasm",
  });
  const loadingPercentage = Math.round(loadingProgression * 100);
  const [isActive, setIsActive] = useState(false);
  const handleActive = () => {
    setIsActive(true);
    setTimeout(() => {
      navigate("/menu");
    }, 600);
  };
  return (
    <div className="home">
      <Unity
        unityProvider={unityProvider}
        style={{ width: "100%", height: "100vh" }}
      />

      {isLoaded ? (
        <div></div>
      ) : (
        <div id="unity-loading-bar">
          <div id="unity-progress-bar-empty">
            <div
              style={{ height: `${loadingPercentage}%` }}
              id="unity-progress-bar-full"
            ></div>
            <div id="unity-progress-bar-mask"></div>
          </div>
        </div>
      )}

      <div className="header-home">
        <div className="header-menu">
          <div
            rel="noopener noreferrer"
            to="/"
            className="flex items-center px-4 -mb-1 border-b-2 dark:border-transparent dark:text-violet-400 dark:border-violet-400"
          >
            <div
              className={`detail ${isActive ? "active" : ""}`}
              onClick={handleActive}
            >
              <p>Lĩnh Vực </p>
              <div className="img">
                <img src={menu} alt="menu" />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
