import { gsap } from "gsap";
import React, { useEffect, useRef, useState } from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import circle from "../../assets/img/About/Frame 44 1.png";
import logo from "../../assets/img/About/logo.png";
export default function About() {
  const [state, setState] = useState(0);
  const { data } = useSelector((store) => store.info);
  const navigate = useNavigate();
  const tl = useRef();
  const aboutRef = useRef();
  const iconList = useRef();
  const q = gsap.utils.selector(iconList);
  const q1 = gsap.utils.selector(aboutRef);
  const closeRef = useRef();
  const closeIconRef = useRef();
  const overlayRef = useRef();
  const logoRef = useRef();
  const contentRef = useRef();
  const circleRef = useRef();
  const handleTab = (e) => {
    let id = e.currentTarget.dataset.id;
    setState(id);
  };
  const handleClose = () => {
    tl.current.reverse();
    setTimeout(() => {
      navigate("/");
    }, 3750);
  };
  useEffect(() => {
    tl.current = gsap
      .timeline()
      .from(overlayRef.current, {
        scaleY: 0,
        duration: 1.2,
        opacity: 0,
        background: "black",
      })
      .from(
        closeIconRef.current,
        {
          opacity: 0,
          rotation: "-360",
          duration: 1,
          color: "#ffae0c",
        },
        "-=.5"
      )
      .from(
        closeRef.current,
        { y: 10, duration: 0.5, opacity: 0, color: "#ffae0c" },
        "-=.5"
      )
      .from(logoRef.current, { scale: 0, duration: 1.5, opacity: 0 }, "-=1")
      .from(q(".icon"), 1, { x: 30, opacity: 0, stagger: 0.3 }, "-=.5")
      .from(q1(".about-item"), 1, { y: 30, opacity: 0, stagger: 0.3 }, "-=1.5")
      .from(contentRef.current, 1, { y: 30, opacity: 0 }, "-=1.5")
      .from(circleRef.current, 1, { scale: 0 }, "-=1");
  }, []);
  useEffect(() => {
    aboutRef?.current?.addEventListener("scroll", (e) => {
      let scroll = e.target.scrollLeft;
      let e1 = e.target.children[0].offsetLeft;
      let e2 = e.target.children[1].offsetLeft;
      let e3 = e.target.children[2].offsetLeft;
      if (
        Math.floor(scroll) + 30 + 1 - e1 === 0 ||
        Math.floor(scroll) + 30 + 1 - e1 === 1
      ) {
        setState(0);
      } else if (
        Math.floor(scroll) + 30 + 1 - e2 === 0 ||
        Math.floor(scroll) + 30 + 1 - e2 === 1
      ) {
        setState(1);
      } else if (
        Math.floor(scroll) + 30 + 1 - e3 === 0 ||
        Math.floor(scroll) + 30 + 1 - e3 === 1
      ) {
        setState(2);
      } else {
        setState(3);
      }
    });
    return () => {
      aboutRef?.current?.addEventListener("scroll", (e) => {
        let scroll = e.target.scrollLeft;
        let e1 = e.target.children[0].offsetLeft;
        let e2 = e.target.children[1].offsetLeft;
        let e3 = e.target.children[2].offsetLeft;
        let e4 = e.target.children[3].offsetLeft;
        if (e1 - 30 === scroll) {
          setState(0);
        } else if (e2 - 30 === scroll) {
          setState(1);
        } else if (e3 - 30 === scroll) {
          setState(2);
        } else if (e4 - 97.4000244140625 === scroll) {
          setState(3);
        }
      });
    };
  }, []);
  return (
    <div className="about main-wrapper" ref={overlayRef}>
      <div className="container">
        <div className="head">
          <div className="about-left" onClick={handleClose}>
            <div>
              <button>
                <i className="fa fa-times" ref={closeIconRef}></i>{" "}
                <span ref={closeRef}>Close</span>
              </button>
            </div>
          </div>
          <div className="about-center" ref={logoRef} onClick={handleClose}>
            <div className="flex items-center px-4 -mb-1 border-b-2 dark:border-transparent">
              <img src={logo} alt="logo" />
              <p>Dai Quoc Viet Corp</p>
            </div>
          </div>
          <div className="about-right">
            <div className="contact-list-2" ref={iconList}>
              <a href="tel:+123456789" className="icon">
                <i className="fa fa-phone"></i>
              </a>
              <a href="mailto:m.bluth@example.com" className="icon">
                <i className="fa fa-envelope"></i>
              </a>
              <a
                href="https://www.facebook.com/daiquocvietcorp"
                className="icon"
              >
                <i className="fab fa-facebook"></i>
              </a>
            </div>
          </div>
        </div>
        <div className="about-us" ref={aboutRef}>
          <div
            className={`about-item ${state == 0 ? "active" : null}`}
            data-id="0"
            onClick={handleTab}
          >
            <img src={data[0]?.image} alt="1" />
            <div className="num">01</div>
          </div>
          <div
            className={`about-item ${state == 1 ? "active" : null}`}
            data-id="1"
            onClick={handleTab}
          >
            <img src={data[1]?.image} alt="2" />
            <div className="num">02</div>
          </div>
          <div
            className={`about-item ${state == 2 ? "active" : null}`}
            data-id="2"
            onClick={handleTab}
          >
            <img src={data[2]?.image} alt="3" />
            <div className="num">03</div>
          </div>
          <div
            className={`about-item ${state == 3 ? "active" : null}`}
            data-id="3"
            onClick={handleTab}
          >
            <img src={data[3]?.image} alt="4" />
            <div className="num">04</div>
          </div>
        </div>
        <div className="about-content" ref={contentRef}>
          <h2>{data[state]?.Title}</h2>
          {data[state]?.details.map((e, index) => {
            if (!e.title) {
              return <p key={index}>{e.content}</p>;
            } else {
              return (
                <p key={index}>
                  <span>{e.title}:</span> {e.content}
                </p>
              );
            }
          })}
        </div>
      </div>
      <div className="circle" ref={circleRef}>
        <img src={circle} alt="circle" />
      </div>
    </div>
  );
}
