import React from "react";
import { useEffect } from "react";
import { useRef } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../../../../assets/img/logo.png";
import gsap from "gsap";

export default function Header() {
  //useRef, animation with gsap
  const tl = useRef();
  const menuLeftRef = useRef();
  const closeIconRef = useRef();
  const logoRef = useRef();

  const navigate = useNavigate();

  //animation when loading into page

  useEffect(() => {
    tl.current = gsap
      .timeline()
      .fromTo(
        menuLeftRef.current,
        {
          opacity: 0,
          y: 40,
        },
        { opacity: 1, y: 0, duration: 0.75, delay: 1 }
      )
      .fromTo(
        closeIconRef.current,
        {
          opacity: 0,
          rotate: "-360",
          y: -60,
        },
        { opacity: 1, rotate: 0, y: 0, duration: 1.5 },
        "-=1"
      )
      .fromTo(
        logoRef.current,
        { y: 40, opacity: 0 },
        { y: 0, opacity: 1, duration: 1.5 },
        "-=2"
      );

    gsap
      .timeline()
      .fromTo(
        gsap.utils.selector(".social-list")(".social-icon"),
        1,
        { x: "110%", opacity: 0 },
        { x: 0, opacity: 1, stagger: 0.5 },
        "+=1"
      );
  });

  // animation when close page
  const handleClose = () => {
    gsap.timeline().to(".wraper", { y: 120, duration: 1, opacity: 0 });
    gsap
      .timeline()
      .to(".nav", { y: "-100vh", opacity: 0, duration: 2.5 }, "-=0.5");
    gsap.timeline().to(".nav-image", { opacity: 0, duration: 1 });
    gsap
      .timeline()
      .to(".social-list", { opacity: 0, x: "105%", duration: 1.5 });
    gsap.timeline().to(".navigation", { opacity: 0, duration: 1 });
    gsap
      .timeline()
      .to(gsap.utils.selector(".social-list")(".social-icon"), 1, {
        x: "110%",
        opacity: 0,
        duration: 1,
        stagger: 0.5,
      });

    tl.current.reverse();
    setTimeout(() => {
      navigate("/");
    }, 1800);
  };

  //animation when click icon
  const handleClickIcon = () => {
    gsap.timeline().to(".wraper", { y: 120, duration: 1, opacity: 0 });
    gsap
      .timeline()
      .to(".nav", { y: "-100vh", opacity: 0, duration: 2.5 }, "-=0.5");
    gsap.timeline().to(".nav-image", { opacity: 0, duration: 1 });
    gsap
      .timeline()
      .to(".social-list", { opacity: 0, x: "105%", duration: 1.5 });
    gsap.timeline().to(".navigation", { opacity: 0 });
    gsap
      .timeline()
      .to(gsap.utils.selector(".social-list")(".social-icon"), 1, {
        x: "105%",
        opacity: 0,
        stagger: 0.3,
      });

    tl.current.reverse();
    setTimeout(() => {
      navigate("/about");
    }, 1800);
  };
  return (
    <div className="header-template">
      <div className="header-menu">
        <div className="menu-left" ref={menuLeftRef} onClick={handleClose}>
          <button>
            <i className="fa fa-times" ref={closeIconRef}></i>{" "}
            <span className="span-close">Close</span>
          </button>
        </div>
        <div className="menu-right" onClick={handleClickIcon}>
          <div className="header-logo" ref={logoRef}>
            <img src={logo} alt="logo" />
            <p>DaiQuocVietCorp</p>
          </div>
        </div>
      </div>
    </div>
  );
}
