import React, { forwardRef, useEffect } from 'react';
import './serviceItem.scss';
import AOS from 'aos';
import 'aos/dist/aos.css';
import textIMG from "../../assets/img/menu/test.png";

function ServiceItem({ id, name, content, src, type,overlay, ...rest }, ref) {
    useEffect(() => {
        AOS.init();
        document.querySelectorAll('img').forEach((img)=>{
            img.addEventListener("load",()=>{
              AOS.refresh();
            })
          });
    }, []);
    return (
        <div className='service-item' {...rest} ref={ref} data-aos="fade-up" data-aos-duration={type ? 1200 : 800} data-aos-easing="ease-out-cubic" data-aos-id="super-duper">
            <div className="overlay-img">
                <img src={overlay} alt="overlay" />
            </div>
            <h2 className="number">0{id}</h2>
            <div className="service-item__wrapper">
            <div className="img">
                <img src={src} alt={id} />
            </div>
            <div className="content">
                <h2>{name}</h2>
                <p>
                    {content}
                </p>
            </div>
            </div>

        </div>
    );
}
export default forwardRef(ServiceItem);
