import gsap from "gsap";
import { useEffect, useLayoutEffect, useRef } from "react";
import {  useSelector } from "react-redux";
import { useLocation, useNavigate, useParams } from "react-router-dom";
//components
import PostItem from "../../components/PostItem/PostItem";

//image
import imageNextPage from "../../assets/img/icon/nextPage.svg";
import imagePreviousPage from "../../assets/img/icon/previousPage.svg";

export default function DetailPage() {
  let { detailID } = useParams();
  const { data } = useSelector((store) => store.posts);
  let dataPost = [];
  const navigate = useNavigate();
  const location = useLocation();
  //useRef
  const wraperRef = useRef();
  const navRef = useRef();
  //page next,previous and index
  let pathPrevious = detailID * 1 - 1;
  let pathNext = detailID * 1 + 1;
  let indexPage = null;
  //animation loading into page
  useEffect(() => {
    detailID = detailID * 1;
    if (detailID === dataPost.id && dataPost.model_3d?.mp4_format)
      gsap.timeline().fromTo("#root", { opacity: 0 }, { opacity: 1 });
    gsap
      .timeline()
      .fromTo(".navigation", { opacity: 0 }, { opacity: 1, duration: 1.5 });

    gsap
      .timeline()
      .fromTo(
        wraperRef.current,
        { y: 450, opacity: 0 },
        { y: 0, opacity: 1, duration: 1.5 }
      );
    gsap
      .timeline()
      .from(
        navRef.current,
        { y: "-100vh", opacity: 0 },
        { y: 0, opacity: 1, duration: 1.5 }
      );
  });

  useLayoutEffect(() => {
    document.documentElement.scrollTo(0, 0);
  }, [location.pathname]);
  // console.log(dataPost);
  //get data from store redux with id = useParams react-router-dom v6

  if (data && detailID) {
    detailID = detailID * 1;
    for (let index = 0; index < data.length; index++) {
      if (data[index].id == detailID) {
        dataPost = data[index];
        indexPage = index + 1;

        if (index === 0) {
          pathPrevious = data[data.length - 1].id;
        } else {
          pathPrevious = data[index - 1].id;
        }
        if (index === data.length - 1) {
          pathNext = data[0].id;
        } else {
          pathNext = data[index + 1].id;
        }
      }
    }
  }

  //handle click next and previous page
  const handleClickNextPage = (pathNext) => {
    navigate(`/detail/${pathNext}`);
  };
  const handleClickPreviousPage = (pathPrevious) => {
    navigate(`/detail/${pathPrevious}`);
  };

  const mouseOver = (e) => {
    const pageCount = document.querySelector(`.page`);
    const svgNextFooter1 = document.querySelector(`.svgNextFooterPath1`);
    const svgNextFooter2 = document.querySelector(`.svgNextFooterPath2`);
    const titleTemplate = document.querySelector(`.title-template`);

    pageCount.style.color = "#333333";
    titleTemplate.style.color = "#333333";

    svgNextFooter1.setAttribute("fill", "#55A36F");
    svgNextFooter1.setAttribute("stroke", "#55A36F");
    svgNextFooter2.setAttribute("fill", "#55A36F");
  };
  const mouseOut = (e) => {
    const pageCount = document.querySelector(`.page`);
    const svgNextFooter1 = document.querySelector(`.svgNextFooterPath1`);
    const svgNextFooter2 = document.querySelector(`.svgNextFooterPath2`);
    const titleTemplate = document.querySelector(`.title-template`);

    pageCount.style.color ="#FFD253";
    titleTemplate.style.color = "#FFFFFF";
    svgNextFooter1.setAttribute("fill", "#FFD253");
    svgNextFooter1.setAttribute("stroke", "#FFD253");
    svgNextFooter2.setAttribute("fill", "#FFD253");
  };
  console.log(dataPost);


  useEffect(()=>{
    window.addEventListener("scroll",()=>{
      const scroll = window.scrollY;
      const navigate= document.querySelector(".navigation");

      const footer= document.querySelector(".footer-detail").offsetTop;
      if(scroll>=footer){
        navigate.style.display="none";
      }else{
        navigate.style.display="block";

      }
    })
  },[])

  return (
    <>
      {dataPost.model_3d?.mp4_format && (
        <>
          {/* <img
            src={dataPost.model_3d.url}
            alt=""
            className="nav-image"
            ref={imageRef}
          /> */}
          {dataPost.model_3d?.mp4_format && (

            <video
              key={dataPost.id}
              className="nav-image"
              autoPlay
              loop
              muted
              ref={navRef}
            >
              <source
                src={dataPost.model_3d.mp4_format}
                type="video/mp4"
              />
            </video>
          )}
          <div className="navigation">
            <div
              className="back"
              onClick={() => {
                handleClickPreviousPage(pathPrevious);
              }}
            >
              <img src={imagePreviousPage} alt="" />
              <p>Back</p>
            </div>

            <div
              className="next"
              onClick={() => {
                handleClickNextPage(pathNext);
              }}
            >
              <p>Next</p>
              <img src={imageNextPage} alt="" />
            </div>
          </div>

          <div className="temp-overlay"></div>
          <div className="wraper" ref={wraperRef}>
            <div className="heading">
              {dataPost.icon && (
                <img src={dataPost.icon} alt="1" className="heading-image" />
              )}
              {dataPost.mobile_icon && (
                <img src={dataPost.mobile_icon} alt="2" className="heading-image-mobile" />
              )}


              {dataPost && <h1>{dataPost.name}</h1>}
            </div>
            <div className="section">
              <div className="item">
                <ul>
                  {dataPost.contents &&
                    dataPost.contents.map((item, index) => {
                      return (
                        <PostItem data={item} key={index} id={index}></PostItem>
                      );
                    })}
                </ul>
              </div>
            </div>
            <div
              className="footer-detail"
              onMouseOver={(e) => mouseOver(e)}
              onMouseOut={(e) => mouseOut(e)}
            >
              <div className="footer-template">
                <div className="left">
                  <div className="page">{dataPost.id}/8</div>
                  {dataPost && (
                    <div className="title-template">{dataPost.name}</div>
                  )}
                </div>
                <div className="right">
                  <div onClick={() => handleClickNextPage(pathNext)}>
                    <svg
                      width="104"
                      height="104"
                      viewBox="0 0 104 104"
                      fill="none"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        d="M1.11914 51.7875C1.11914 65.3296 6.38355 78.0664 15.9378 87.636C25.492 97.2057 38.2091 102.479 51.7308 102.479C65.2525 102.479 77.9696 97.2057 87.5238 87.636C97.078 78.0664 102.342 65.3296 102.342 51.7875C102.342 38.2454 97.078 25.5085 87.5238 15.9389C77.9696 6.36923 65.2525 1.0957 51.7308 1.0957C38.2091 1.0957 25.492 6.36923 15.9378 15.9389C6.38355 25.5085 1.11914 38.2454 1.11914 51.7875ZM51.7308 98.4916C26.0301 98.4916 5.10361 77.5336 5.10361 51.7875C5.10361 26.0413 26.0301 5.08337 51.7308 5.08337C77.4315 5.08337 98.358 26.0413 98.358 51.7875C98.358 77.5336 77.4315 98.4916 51.7308 98.4916Z"
                        fill="url(#paint0_linear_558_156)"
                        stroke="url(#paint1_linear_558_156)"
                        stroke-width="2"
                        stroke-miterlimit="10"
                        className="svgNextFooterPath1"
