import {  useState } from "react";
import {  useNavigate } from "react-router-dom";
import menu from "../../assets/img/menu.png";
import React from "react";
import { useUnityContext } from "react-unity-webgl";
import { Unity } from "react-unity-webgl";
import { useSelector } from "react-redux";

export default function Home(props) {
  const { data } = useSelector(store => store.home);
  // console.log(data)
const getDataUrl = (data) => {
    const dataUrl = {
      "dataUrl": data.model.file_build_data,
      "loaderUrl": data.model.file_build_loader,
      "frameworkUrl": data.model.file_build_framework,
      "codeUrl": data.model.file_build_wasm
    }
    console.log(dataUrl)
    return dataUrl;
  }
 const dataLotus = async () => {
     try {
        let result = await getDataUrl();
      console.log(result) 
      return result;
     }
    catch(error) {
        console.log(error)
    }
} 

 const { unityProvider, isLoaded, loadingProgression } = useUnityContext({
  //   loaderUrl: "Lotus/Build/BUILD.loader.js",
  //   dataUrl: "Lotus/Build/BUILD.data",
  //   frameworkUrl: "Lotus/Build/BUILD.framework.js",
  //   codeUrl: "Lotus/Build/BUILD.wasm",

  dataUrl: dataLotus.dataUrl,
  loaderUrl: dataLotus.loaderUrl,
  frameworkUrl: dataLotus.frameworkUrl,
  codeUrl: dataLotus.codeUrl,
});

// console.log(data.model.file_build_data)


  const loadingPercentage = Math.round(loadingProgression * 100);
  const [isActive, setIsActive] = useState(false);
  const navigate = useNavigate();
  const handleActive = () => {
    setIsActive(true);
    setTimeout(() => {
      navigate("/menu");
    }, 600);
  };

  return (
    <div className="home">
      <Unity
        unityProvider={unityProvider}
        style={{ width: "100%", height: "100vh" }}
      />


      {isLoaded ? (
        <div></div>
      ) : (
        <div id="unity-loading-bar">
          <div id="unity-progress-bar-empty">
            <div
              style={{ width: `${loadingPercentage}%` }}
              id="unity-progress-bar-full"
            ></div>
          </div>
        </div>
      )}

      <div className="header-home">
        <div className="header-menu">
          <div
            rel="noopener noreferrer"
            to="/"
            className="flex items-center px-4 -mb-1 border-b-2 dark:border-transparent dark:text-violet-400 dark:border-violet-400"
          >
            <div
              className={`detail ${isActive ? "active" : ""}`}
              onClick={handleActive}
            >
              <p>Lĩnh Vực </p>
              <div className="img">
                <img src={menu} alt="menu" />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
