import  { useRef, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import logo from "../../assets/img/logo.png";
import background from "../../assets/img/background.png";
import menu from "../../assets/img/menu.png";
import { useUnityContext} from "react-unity-webgl";
import React, { useEffect } from "react";
import  {Unity, UnityContext } from "react-unity-webgl";

export default function Home(props) {
	const { unityProvider } = useUnityContext({
		loaderUrl: "Build/BUILD.loader.js",
		dataUrl: "Build/BUILD.data",
		frameworkUrl: "Build/BUILD.framework.js",
		codeUrl: "Build/BUILD.wasm",
	});
	const [isActive, setIsActive] = useState(false);
	const navigate = useNavigate();
	const handleActive = () => {
		setIsActive(true);
		setTimeout(() => {
			navigate('/menu');
		}, 600);
	};


	// var canvas = document.querySelector("#unity-canvas");
	// var loadingBar = document.querySelector("#unity-loading-bar");
	// var progressBarFull = document.querySelector("#unity-progress-bar-full");
	// loadingBar.style.display = "block";

	// var script = document.createElement("script");
	// script.src = "Build/BUILD.loader.js";
	// script.onload = () => {
	//   createUnityInstance(canvas, config, (progress) => {
	// 	progressBarFull.style.width = 100 * progress + "%";
	//   }).then((unityInstance) => {
	// 	loadingBar.style.display = "none";
	//   }).catch((message) => {
	// 	console.log(message);
	//   });
	// };
	// document.body.appendChild(script);




	useEffect(function () {
		unityProvider.on("progress", function (progression) {
		  console.log(progression);
		});
		return function () {
		  unityProvider.removeEventListener("progress");
		};
	  }, []);












	return (
		<div className="home">
			          	<div id="unity-loading-bar">
	    <div id="unity-progress-bar-empty">
			<div id="unity-progress-bar-full"></div>
	    </div>
		</div>
			<Unity unityProvider={unityProvider}  style={{width:"100%",height:"100vh"}}/>;
			<div className="header-home">
				<div className="header-menu">
					<div
						rel="noopener noreferrer"
						to="/"
						className="flex items-center px-4 -mb-1 border-b-2 dark:border-transparent dark:text-violet-400 dark:border-violet-400"
					>
						<div className={`detail ${isActive ? 'active' : ''}`} onClick={handleActive} >
							<p>Lĩnh Vực </p>
							<div className="img"  >
								<img src={menu} alt="menu" />
							</div>
						</div>
					</div>
				</div>
			</div>

		</div>
	);
}
