import React, { useEffect } from "react";
import AOS from "aos";
import "aos/dist/aos.css";

export default function PostItem({ data, id }) {


  //initialize animation with aos
  useEffect(() => {
    AOS.init({
      mirror: true,
      once: false,
      duration: 1200,
    });
    AOS.refresh();
    document.querySelectorAll("img").forEach((img) => {
      img.addEventListener("load", () => {
        AOS.refresh();
      });
    });
  }, []);

  return (
    <li>
      {data.title && (
        <h2 data-aos="fade-up">
          {id + 1}. {data.title}
        </h2>
      )}
      <p data-aos="fade-up">{data.description}</p>

      <figure>
        <img src={data.image} alt={id} data-aos="fade-up" />
      </figure>
    </li>
  );
}
